import React from 'react';
import emailjs from 'emailjs-com';

function ContactForm() {
  const sendEmail = (e) => {
    e.preventDefault();

    // Get the form data
    const formData = new FormData(e.target);

    // Send the email using EmailJS
    emailjs
      .sendForm('service_09mdgfl', 'template_hxitikd', formData, 'I59mHqfMF093XbTav')
      .then(
        (result) => {
          console.log('Email sent successfully:', result.text);
          // Add any success message or redirect logic here
        },
        (error) => {
          console.error('Email sending failed:', error);
          // Add error handling or display an error message
        }
      );
  };

  return (
    <div className='flex flex-col justify-center items-center'>
      <p className='py-3 text-4xl font-bold text-center'>Contact</p>
      <p className='py-4'>Send me a message</p>
      <form onSubmit={sendEmail}>
        <input className='p-2 border-2 text-black' type="text" placeholder='Name' name='name' />
        <input className='my-4 p-2 border-2 text-black' type="email" placeholder='Email' name='email' />
        <textarea className='p-2 border-2' name="message" rows="10" placeholder='Message'></textarea>
        <button className='border-2 p-4 bg-cyan-500 py-3 my-8 mx-auto flex items-center rounded-full' type="submit">
          Send
        </button>
      </form>
    </div>
  );
}

export default ContactForm;
