import React from 'react';
import './App.css';
import {BsFillMoonStarsFill} from 'react-icons/bs';


function App() {
  return (
    <div className="App">
      <section className='min-h-screen'>
        <nav className='py-10 mb-12 flex justify-between'>
          <h1 className='text-xl font-bold'>Daniel Mwihoti Portfolio</h1>
          <ul className='flex items-center'>
            <li><BsFillMoonStarsFill className='cursor-pointer text-2xl'/> </li>
            <li><a className='bg-gradient-to-r from-cyan-500 to-teal-500 text-white px-4 py-2 rounded-md ml-8' href="">
              Resume</a></li>
          </ul>
        </nav>
        <div>
         <h2>Daniel Edward Mwihoti </h2>
         <h3> Frontend Developer and Netwrok administrator</h3>
         <p>
          Freelancer providing services for programming and website design. Join
          me below and get's cracking!
         </p>
          </div>

      </section>
    </div>
  );
}

export default App;
