import React from 'react';
import emailjs from 'emailjs-com';

const Contact = () => {

const serviceId = 'service_09mdgfl';
const templateId = 'template_hxitikd';
const userId = 'I59mHqfMF093XbTav';

const handleSubmit = (event) => {
    event.preventDefault(); // Prevent the default form submission

    // Get the form element by its ID
    const form = event.target;

    // Use emailjs to send the form data
    emailjs
      .sendForm(serviceId, templateId, form, userId)
      .then((response) => {
        console.log('Email sent successfully:', response);
        // Add any success message or redirection logic here
      })
      .catch((error) => {
        console.error('Email sending failed:', error);
        // Handle the error, e.g., display an error message
      });
  };

  return (
    <div className=' flex-col mx-auto text-center max-w-[600px]'>
        <div className='flex flex-col justify-center  items-center'>
      <p className='py-3 text-4xl font-bold text-center'>Contact</p>
      <p className='py-4'>Send me a message</p>
      </div>
      <form id="myForm" >
        <input className='p-2 border-2 text-black' type="text" placeholder='Name' name='name' />
        <input className='my-4 p-2 border-2 text-black' type="email" placeholder='Email' name='email' />
        <textarea className='p-2 border-2' name="message" rows="10" placeholder='Message'></textarea>
        
      </form>
      <button className='border-2 p-4 bg-cyan-500 py-3 my-8 mx-auto flex items-center rounded-full' type="submit" onClick={handleSubmit}>
          Send
        </button>
    </div>
  );
}

export default Contact;
