import React, { useRef } from 'react';
import emailjs from 'emailjs-com';

const Contact = () => {
    const form = useRef();

    const sendEmail = (e) => {
      e.preventDefault();
  
      emailjs.sendForm('service_09mdgfl', 'template_hxitikd', form.current, 'I59mHqfMF093XbTav')
        .then((result) => {
            console.log(result.text,"sent");
        }, (error) => {
            console.log(error.text);
        });
    };
   

  return (
    <>
   <div className='text-center bg-[#ebebeb]'>
      <form ref={form} onSubmit={sendEmail} className=" mx-auto">
        <div>
      <label className=' shadow-md text-2xl'>Name</label>
      <input type="text" name="user_name" className='border w-96 p-15 py-3 m-5' />
      </div>
      <div>
      <label className='shadow-md text-2xl'>Email</label>
      <input type="email" name="user_email" className='border w-96 m-5 py-3 p-15 ' />
      </div>
      <div>
      <label className='shadow-md text-2xl'>Message</label> 
      <textarea type=" text" name="message" className='border w-100 m-2' />
      </div>
     
      
    </form>
   
    </div>
    <div className=' flex justify-center'>
    <button type="button" onClick={sendEmail} className="  justify-self-center p-4 bg-zinc-800 rounded-lg text-lg hover:font-bold text-sky-500" >
        Send
      </button>
      </div>
      <div>
                <label class="dark:text-gray-200" for="username">Username</label>
                <input id="username" type="text" class="block  px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"/>
            </div>

    </>
  );
}

export default Contact;
