import React, { useRef } from 'react';
import emailjs from 'emailjs-com';

const Contact = () => {
    const form = useRef();

    const sendEmail = (e) => {
      e.preventDefault();
  
      emailjs.sendForm('service_09mdgfl', 'template_hxitikd', form.current, 'I59mHqfMF093XbTav')
        .then((result) => {
            console.log(result.text,"sent");
        }, (error) => {
            console.log(error.text);
        });
    };
   

  return (
    <>
   <div className='text-center bg-[#ebebeb]'>
      <form ref={form} onSubmit={sendEmail} className=" mx-auto">
      
      <div>
                <label class="text-white dark:text-gray-200" for="username">Username</label>
                <input id="username" type="text" class="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"/>
            </div>
      
    </form>
   
    </div>
    <div className=' flex justify-center'>
    <button type="button" onClick={sendEmail} className="  justify-self-center p-4 bg-zinc-800 rounded-lg text-lg hover:font-bold text-sky-500" >
        Send
      </button>
      </div>
      

    </>
  );
}

export default Contact;
